      <!-- <div class="py-16 flex flex-col justify-center items-center">
      </div> -->
    </footer>
  </div>
</template>

<script lang="ts">
export default {
  name: 'Index',
  data() {
    return {
      auto: true,
      light: false,
      dark: false,
    }
  },
  mounted() {},
  methods: {
    toggle(event) {
      if (event.target.value === 'auto') {
        this.auto = true
        this.light = false
        this.dark = false
      }
      if (event.target.value === 'light') {
        this.auto = false
        this.light = true
        this.dark = false
      }
      if (event.target.value === 'dark') {
        this.auto = false
        this.light = false
        this.dark = true
      }
    },
    scrollToTop() {
      window.scrollTo(0, 0)
    }
  },
}
</script>

